package PracticasBuclesU5A;

import java.util.*;

public class VeinteNumeros {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int contador = 1; // iniciamos el contador

		while (contador <=20) { /* mientras que el contador
		sea igual o menor que 20 seguira el bucle */
			
			System.out.println(contador); /* imprime el numero actual */
		
			contador++; //Incrementa el contador para pasar al siguiente nÃºmero
		}
		
		

	
	}
	

}
